import { Component } from '@angular/core';
import { NgForm, FormControl, Validators } from '@angular/forms';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent {
  contrasenaControl = new FormControl('', [
    Validators.required,
    Validators.minLength(8),
    Validators.maxLength(20),
    Validators.pattern('^(?=.*[a-z])(?=.*[A-Z])(?=.*\\d)[a-zA-Z\\d]{8,20}$')
  ]);

  contrasenaValida = false;

  constructor(private http: HttpClient) {}

  registrarUsuario(form: NgForm) {
    if (form.invalid) {
      return;
    }

    const usuario = {
      nombre: form.value.nombre,
      contrasena: form.value.contrasena,
      sexo: form.value.sexo
    };

    this.http.post('https://localhost:7271/api/Usuario', usuario).subscribe(
      response => {
        console.log(response);
      },
      error => {
        console.log(error);
      }
    );
  }

  validarContrasena() {
    this.contrasenaValida = this.contrasenaControl.valid;
  }
}
<div class="container" style="margin-top: 30px;">
  <h2>Registro de Usuario</h2>
  <form (ngSubmit)="registrarUsuario(f)" #f="ngForm">
    <input type="text" id="nombre" name="nombre" placeholder="Nombre" ngModel required>
    <input type="password" id="contrasena"  name="contrasena" placeholder="Contraseña" ngModel required minlength="8" maxlength="20" pattern="^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)[a-zA-Z\d]{8,20}$"
      #contrasenaInput="ngModel">
    <div *ngIf="contrasenaInput.invalid && (contrasenaInput.dirty || contrasenaInput.touched)">
      <div *ngIf="contrasenaInput.hasError('required')" class="alert alert-danger">La contraseña es requerida.</div>
      <div *ngIf="contrasenaInput.hasError('minlength')" class="alert alert-danger">La contraseña debe tener al menos 8 caracteres.</div>
      <div *ngIf="contrasenaInput.hasError('maxlength')" class="alert alert-danger">La contraseña no puede tener más de 20 caracteres.</div>
      <div *ngIf="contrasenaInput.hasError('pattern')" class="alert alert-danger">La contraseña debe incluir al menos una mayúscula, una minúscula y un número.</div>
    </div>
    <input type="text" id="sexo" name="sexo" placeholder="Sexo" ngModel required>
    <button type="submit" [disabled]="f.invalid || contrasenaInput.invalid">Registrar</button>
  </form>
</div>
