import { Component } from '@angular/core';
import { NgForm } from '@angular/forms';
import { apiservice } from '../apiservice.service';
import { Ingrediente } from '../interfaces/ingrediente.interface';

@Component({
  selector: 'app-crear-ingrediente',
  templateUrl: './crear-ingrediente.component.html',
  styleUrls: ['./crear-ingrediente.component.css']
})
export class CrearIngredienteComponent {

  constructor(private apiservice: apiservice) {}

  crearIngrediente(form: NgForm) {
    if (form.invalid) {
      return;
    }

    const nombre = form.value.nombre;
    const tipo = form.value.tipo;
    const calorias = form.value.calorias;

    const nuevoIngrediente: Ingrediente = {
      nombre: nombre,
      tipo: tipo,
      calorias: calorias
    };

    this.apiservice.crearIngrediente(nuevoIngrediente).subscribe(
      response => {
        console.log(response);
        // Realizar acciones adicionales después de crear el ingrediente si es necesario
        form.resetForm(); // Reiniciar el formulario
      },
      error => {
        console.log(error);
      }
    );
  }

}
