import { Component } from '@angular/core';
import { NgForm, FormControl, Validators } from '@angular/forms';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent {
  contrasenaControl = new FormControl('', [
    Validators.required,
    Validators.minLength(8),
    Validators.maxLength(20),
    Validators.pattern('^(?=.*[a-z])(?=.*[A-Z])(?=.*\\d)[a-zA-Z\\d]{8,20}$')
  ]);

  contrasenaValida = false;

  constructor(private http: HttpClient) {}

  registrarUsuario(form: NgForm) {
    if (form.invalid) {
      return;
    }

    const usuario = {
      nombre: form.value.nombre,
      contraseña: form.value.contraseña,
      sexo: form.value.sexo
    };

    this.http.post('https://localhost:7271/api/Usuario', usuario).subscribe(
      response => {
        console.log(response);
      },
      error => {
        console.log(error);
      }
    );
  }

  validarContrasena() {
    this.contrasenaValida = this.contrasenaControl.valid;
  }
}
<div class="container">
  <h2>Registro de Usuario</h2>
  <form (ngSubmit)="registrarUsuario(f)" #f="ngForm">
    <input type="text" name="nombre" placeholder="Nombre" ngModel required>
    <input type="password" name="contrasena" placeholder="Contrasena" ngModel required minlength="8" maxlength="20" pattern="^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)[a-zA-Z\d]{8,20}$">
    <div *ngIf="f.controls.contrasena.invalid && (f.controls.contrasena.dirty || f.controls.contrasena.touched)">
      <div *ngIf="f.controls.contrasena.errors.required" class="alert alert-danger">La contrasena es requerida.</div>
      <div *ngIf="f.controls.contrasena.errors.minlength" class="alert alert-danger">La contrasena debe tener al menos 8 caracteres.</div>
      <div *ngIf="f.controls.contrasena.errors.maxlength" class="alert alert-danger">La contrasena no puede tener mas de 20 caracteres.</div>
      <div *ngIf="f.controls.contrasena.errors.pattern" class="alert alert-danger">La contrasena debe incluir al menos una mayuscula, una minuscula y un numero.</div>
    </div>
    <input type="text" name="sexo" placeholder="Sexo" ngModel required>
    <button type="submit" [disabled]="f.invalid || f.controls.contrasena.invalid">Registrar</button>
  </form>
</div>
