import { Component } from '@angular/core';
import { NgForm } from '@angular/forms';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent {
  constructor(private http: HttpClient) { }

  registrarUsuario(form: NgForm) {
    if (form.invalid) {
      return;
    }

    const usuario = {
      nombre: form.value.nombre,
      contraseña: form.value.contraseña,
      sexo: form.value.sexo
    };

    this.http.post('https://localhost:7271/api/Usuario', usuario)
      .subscribe(response => {
        console.log(response);
      });
  }
}
<div class="container">
  <h2>Registro de Usuario</h2>
  <form (ngSubmit)="registrarUsuario(f)" #f="ngForm">
    <input type="text" name="nombre" placeholder="Nombre" ngModel required>
    <input type="password" name="contraseña" placeholder="Contraseña" ngModel required>
    <input type="text" name="sexo" placeholder="Sexo" ngModel required>
    <button type="submit">Registrar</button>
  </form>
</div>
