import { Component } from '@angular/core';
import { NgForm } from '@angular/forms';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent {
  constructor(private http: HttpClient) { }

  registrarUsuario(form: NgForm) {
    if (form.invalid) {
      return;
    }

    const usuario = {
      nombre: form.value.nombre,
      contraseña: form.value.contraseña,
      sexo: form.value.sexo
    };

    this.http.post('https://localhost:7271/api/Usuario', usuario)
      .subscribe(response => {
        console.log(response);
      });
  }
}
<form (ngSubmit)="registrarUsuario(f)" #f="ngForm">
  <div>
    <label for="nombre">Nombre:</label>
    <input type="text" id="nombre" name="nombre" ngModel required>
  </div>
  <div>
    <label for="contraseña">Contraseña:</label>
    <input type="password" id="contraseña" name="contraseña" ngModel required>
  </div>
  <div>
    <label for="sexo">Sexo:</label>
    <input type="text" id="sexo" name="sexo" ngModel required>
  </div>
  <button type="submit">Registrar</button>
</form>
