import { Component, OnInit } from '@angular/core';
import { apiservice } from '../apiservice.service';

@Component({
  selector: 'app-testpage',
  templateUrl: './testpage.component.html',
  styleUrls: ['./testpage.component.css']
})
export class TestpageComponent implements OnInit {
  datos: any;

  constructor(private apiservice: apiservice) {}

  ngOnInit(): void {
    this.apiservice.obtenerDatosDeRecetas().subscribe(
      (data) => {
        this.datos = data;
        console.log(data)
      },
      (error) => {
        console.error('Error al obtener los datos:', error);
      }
    );
    const loggedInUser = localStorage.getItem('loggedInUser');
    if (loggedInUser) {
      this.usuario = JSON.parse(loggedInUser);
    } else {
      // Si no hay usuario logeado, redirigir al componente de login
      this.router.navigate(['/login']);
    }
  }
}
<app-navbar></app-navbar>

<div *ngIf="datos" class="container">
  <h2 class="text-center">Recetas más votadas:</h2>

  <div class="card-container">
    <div class="card" *ngFor="let item of datos">
      <h3>{{ item.nombre }}</h3>
      <p>{{ item.descripcion }}</p>
      <p>Tipo: {{ item.tipo }}</p>
    </div>
  </div>
</div>

<router-outlet></router-outlet>
