import { Component } from '@angular/core';
import { NgForm } from '@angular/forms';
import { HttpClient } from '@angular/common/http';
import { compare } from 'bcryptjs';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  constructor(private http: HttpClient, private router: Router) {}

  async loginUsuario(form: NgForm) {
    if (form.invalid) {
      return;
    }

    const nombre = form.value.nombre;
    const contrasena = form.value.contrasena;

    // Llamar a la API para obtener el usuario por nombre
    try {
      const response = await this.http.get('https://localhost:7271/api/GetUsuario?nombre=' + nombre).toPromise();
      if (response) {
        const usuario = response;
        const contraseñaEncriptada = usuario.contrasena;

        const contraseñaCoincide = await compare(contrasena, contraseñaEncriptada);

        if (contraseñaCoincide) {
          // La contraseña es correcta, redirigir a TestpageComponent
          this.router.navigate(['/testpage']);
        } else {
          // La contraseña es incorrecta
          console.log('Contraseña incorrecta');
        }
      } else {
        // No se encontró ningún usuario con ese nombre
        console.log('Usuario no encontrado');
      }
    } catch (error) {
      console.log(error);
    }
  }
}
<div class="container">
  <h2>Login</h2>
  <form (ngSubmit)="loginUsuario(f)" #f="ngForm">
    <input type="text" id="nombre" name="nombre" placeholder="Nombre" ngModel required>
    <input type="password" id="contrasena" name="contrasena" placeholder="Contraseña" ngModel required>
    <button type="submit">Iniciar sesión</button>
  </form>
</div>
