import { Component, OnInit } from '@angular/core';
import { apiservice } from '../apiservice.service';
import { Receta } from '../interfaces/receta.interface';
import { Ingrediente } from '../interfaces/ingrediente.interface';
import { Usuario } from '../interfaces/usuario.interface';
import { Router } from '@angular/router';

@Component({
  selector: 'app-recetas',
  templateUrl: './recetas.component.html',
  styleUrls: ['./recetas.component.css']
})
export class RecetasComponent implements OnInit {
  recetas: Receta[] = [];
  mostrarPestanaCreacion = false;
  usuario: Usuario | null = null;
  ingredientes: Ingrediente[] = [];

  constructor(private apiservice: apiservice, private router: Router) {}

  ngOnInit(): void {
    this.obtenerRecetas();
    const loggedInUser = localStorage.getItem('loggedInUser');
    if (loggedInUser) {
      this.usuario = JSON.parse(loggedInUser);
    } else {
      // Si no hay usuario logeado, redirigir al componente de login
      this.router.navigate(['/login']);
    }
    this.obtenerIngredientes();
  }

  obtenerRecetas(): void {
    this.apiservice.obtenerRecetas().subscribe(
      (data: Receta[]) => {
        this.recetas = data;
      },
      (error: any) => {
        console.error('Error al obtener las recetas:', error);
      }
    );
  }

  obtenerIngredientes(): void {
    this.apiservice.obtenerIngredientes().subscribe(
      (data: Ingrediente[]) => {
        this.ingredientes = data;
      },
      (error: any) => {
        console.error('Error al obtener los ingredientes:', error);
      }
    );
  }

  abrirPestanaCreacion(): void {
    this.mostrarPestanaCreacion = true;
  }

  cerrarPestanaCreacion(): void {
    this.mostrarPestanaCreacion = false;
  }

  crearReceta(form: any): void {
    if (form.valid) {
      // Obtener los IDs de los ingredientes seleccionados
      const ingredientesSeleccionados = this.ingredientes.filter(ingrediente => form.value[ingrediente.id]);
      const ingredientesIds = ingredientesSeleccionados.map(ingrediente => ingrediente.id);

      const nuevaReceta: any = {
        nombre: form.value.nombre,
        descripcion: form.value.descripcion,
        tipo: form.value.tipo,
        url: form.value.url,
        usuarioId: this.usuario?.id || 0,
        ingredientes: ingredientesIds
      };

      this.apiservice.crearReceta(nuevaReceta).subscribe();
      this.cerrarPestanaCreacion();
      this.obtenerRecetas();
      location.reload();
      this.obtenerRecetas();
      location.reload();
    }
  }
}
<app-navbar></app-navbar>

<div class="container">
  <h2>Recetas</h2>

  <button class="btn-create" (click)="abrirPestanaCreacion()">Crear Receta</button>

  <div class="card-container">
    <div class="card" *ngFor="let receta of recetas">
      <h3>{{ receta.nombre }}</h3>
      <p>Tipo: {{ receta.tipo }}</p>
      <p>Descripción: {{ receta.descripcion }}</p>
    </div>
  </div>

  <div class="creacion-overlay" *ngIf="mostrarPestanaCreacion">
    <div class="creacion-modal">
      <h3>Crear Receta</h3>
      <form (ngSubmit)="crearReceta(form)" #form="ngForm">
        <div class="form-group">
          <label for="nombre">Nombre:</label>
          <input type="text" id="nombre" name="nombre" ngModel required>
        </div>
        <div class="form-group">
          <label for="tipo">Tipo:</label>
          <input type="text" id="tipo" name="tipo" ngModel required>
        </div>
        <div class="form-group">
          <label for="descripcion">Descripción:</label>
          <textarea id="descripcion" name="descripcion" ngModel required></textarea>
        </div>
        <div class="form-group">
          <label for="ingredientes">Ingredientes:</label>
          <div *ngFor="let ingrediente of ingredientes">
            <label>
              <input type="checkbox" [name]="ingrediente.id.toString()" [ngModel]="false">
              {{ ingrediente.nombre }}
            </label>
          </div>
        </div>
        <div class="form-group">
          <label for="url">URL:</label>
          <input type="text" id="url" name="url" ngModel required>
        </div>
        <div class="form-group">
          <button type="submit">Crear</button>
          <button type="button" (click)="cerrarPestanaCreacion()">Cancelar</button>
        </div>
      </form>
    </div>
  </div>
</div>
